mapper.select:
select * from tb_contents c join tb_favorite f on c.contents_id =
f.contents_id
where user_id = #{userId}
limit #{paging.offset}, #{paging.recordSize}

select count(*) from tb_contents c join tb_favorite f on c.contents_id =
f.contents_id
where user_id = #{userId}

select c.contents_id, title ,region1, region2, contents_label, item_memo, latitude, longitude
from tb_item i join tb_contents c on i.contents_id = c.contents_id where schedule_id = #{scheduleId} and item_day = #{itemDay}
order by item_sequence
